$(document).ready(function(){$('.nav-btn').on('click',function(){var target=$(this).data('target');$(target).slideToggle(500);});$('.nav-list__link').on('click',function(event){event.preventDefault();if($(window).width()<750){$('.nav-list').css('display', 'none');}});$('.popup-btn').on('click',function(event){event.preventDefault();$('.popup').fadeIn();});$('.popup__close').on('click',function(event){$('.popup').fadeOut();});$(window).scroll(function(){if($(this).scrollTop()>20){$('.header-box').addClass('header-box--fixed').fadeIn('fast');}else{$('.header-box').removeClass('header-box--fixed').fadeIn('fast');};});var $page=$('html, body');$('a[href*="#"]').click(function(){$page.animate({scrollTop:$($.attr(this,'href')).offset().top-135},700);return false;});$(window).scroll(function(){if($(this).width()>749){var info=$('.header').offset(),wScroll=$(this).scrollTop();$('.content-header').css({'transform':'translate(0, -'+(wScroll-info.top)/1.3+'%)'});$('.info-block').css({'opacity':1,'transform':'translateY(-50%)'});} if($(this).width()>1023){$('.info-block').css({'transform':'translateY(-80%)'});} if($(this).scrollTop()<20){$('.info-block').css({'opacity':0});}});});